import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';


export default function HomeScreen({ navigation }) {
return (
<View style={styles.container}>
<Text style={styles.title}>INICIO</Text>
<ScrollView style={styles.cardArea}>
<View style={styles.card}>
<Text style={styles.cardTitle}>Favores cerca de ti</Text>
<View style={styles.item}>
<Text>Art√≠culo</Text>
<TouchableOpacity style={styles.button}><Text>Prestar</Text></TouchableOpacity>
</View>
<View style={styles.item}>
<Text>Raite</Text>
<TouchableOpacity style={[styles.button, styles.offer]}><Text>Ofrecer</Text></TouchableOpacity>
</View>
</View>
</ScrollView>
<TouchableOpacity style={styles.bottomBtn}><Text>Pedir Paro</Text></TouchableOpacity>
</View>
);
}


const styles = StyleSheet.create({
container: { flex:1, backgroundColor:'#bfe1ff', alignItems:'center'},
});
